import React, { useEffect, useState } from 'react';
import { useDispatch } from 'react-redux';
import { useNavigate } from 'react-router-dom';
import { GoogleLogin } from '@react-oauth/google';
import jwt_decode from 'jwt-decode';

import { signin, signup } from "../../actions/authAction"
import { AUTH } from "../../constants/actionTypes"

const initialState = { firstName: '', lastName: '', email: '', password: '', confirmPassword: '' };

const Auth = () => {
    const dispatch = useDispatch();
    const navigate = useNavigate();

    const [form, setForm] = useState(initialState);
    const [isSignup, setIsSignup] = useState(false);
    
   
    const switchMode = () => {
        setForm(initialState);
        setIsSignup((prev) => !prev);
    };
    const handleChange = (e) => setForm({ ...form, [e.target.name]: e.target.value });
    const handleGoogleSuccess = async (res) => {
        const userObj = jwt_decode(res.credential);
        const result = { name: userObj.name, image: userObj.picture, email:userObj}
        try {
            dispatch( {type: AUTH, data: {userObj} });
            navigate('/');
        } catch (error) {
            console.log(error);
        }
    }
    const handleSubmit = (e) => {
        e.preventDefault();
    
        if (isSignup) {
          dispatch(signup(form, navigate));
        } else {
          dispatch(signin(form, navigate));
        }
    }

    return (
    <form style={{width:"50vw"}} onSubmit={handleSubmit}>
        <h1>{ isSignup ? 'Sign Up': 'Sign In' }</h1>
        {   isSignup && (
            <div class="form-group">
            <label for="exampleInputFname">First Name</label>
            <input type="text" class="form-control" id="exampleInputFname" handleChange={handleChange}/>
            <label for="exampleInputLname">Last Name</label>
            <input type="text" class="form-control" id="exampleInputLname" handleChange={handleChange}/>
            </div>
        )
        }
        <div class="form-group">
            <label for="exampleInputEmail1">Email address</label>
            <input type="email" class="form-control" id="exampleInputEmail1" handleChange={handleChange}/>
        </div>
        <div class="form-group">
            <label for="exampleInputPassword1">Password</label>
            <input type= 'password' class="form-control" id="exampleInputPassword1" handleChange={handleChange}/>
        </div>
        <hr/>
        
        <GoogleLogin
            onSuccess={handleGoogleSuccess}
            onError={() => {
                console.log('Login Failed');
            }}
        /> <br/>
        
        <button type="submit" class="btn btn-primary me-4">{isSignup ? 'Sign Up' : 'Sign In'}</button>
        <button type="button" class="btn btn-secondary" onClick={switchMode}>
            { isSignup ? 'Already have an account? Sign in' : "Don't have an account? Sign Up" }
        </button>
    </form>
    );
}

export default Auth;
