import React, { useState } from 'react';
import { useDispatch } from 'react-redux';
import { useNavigate } from 'react-router-dom';

import { signin, signup } from "../../actions/authAction"
import { AUTH } from "../../constants/actionTypes"

const initialState = { firstName: '', lastName: '', email: '', password: '', confirmPassword: '' };

const Auth = () => {
    const dispatch = useDispatch();
    const navigate = useNavigate();

    const [form, setForm] = useState(initialState);
    const [isSignup, setIsSignup] = useState(false);
    const [showPassword, setShowPassword] = useState(false);
   
    const switchMode = () => {
        setForm(initialState);
        setIsSignup((prev) => !prev);
        setShowPassword(false);
    };

    const handleChange = (e) => setForm({ ...form, [e.target.name]: e.target.value });
    const handleShowPassword = () => setShowPassword(!showPassword);

    const handleSubmit = (e) => {
        e.preventDefault();
    
        if (isSignup) {
          dispatch(signup(form, history));
        } else {
          dispatch(signin(form, history));
        }
    }

    return (
    <form>
        <h1>{ isSignup ? 'Sign Up': 'Sign In' }</h1>
        {   isSignup && (
            <div class="form-group">
            <label for="exampleInputFname">First Name</label>
            <input type="text" class="form-control" id="exampleInputFname" handleChange={handleChange}/>
            <label for="exampleInputLname">Last Name</label>
            <input type="text" class="form-control" id="exampleInputLname" handleChange={handleChange}/>
            </div>
        )
        }
        <div class="form-group">
            <label for="exampleInputEmail1">Email address</label>
            <input type="email" class="form-control" id="exampleInputEmail1" handleChange={handleChange}/>
            <small id="emailHelp" class="form-text text-muted">We'll never share your email with anyone else.</small>
        </div>
        <div class="form-group">
            <label for="exampleInputPassword1">Password</label>
            <input type={showPassword ? 'text' : 'password'} class="form-control" id="exampleInputPassword1" handleChange={handleChange} handleShowPassword={handleShowPassword}/>
        </div>
        <button type="submit" class="btn btn-primary">{isSignup ? 'Sign Up' : 'Sign In'}</button>
    </form>
    );
}

export default Auth;
