import React, { useEffect } from 'react';
import { useDispatch } from 'react-redux';
import { getEmps } from "../../actions/empsAction";
import { useSelector } from 'react-redux';
import Employee from './Employee/Employee.js'
const Employees = () => {
    const dispatch = useDispatch();
	useEffect(() => {
		dispatch(getEmps());
	  }, [dispatch]);
    const emps = useSelector((state) => state.emps);
    const [searchQuery, setSearchQuery] = useState({search: '', role: ''})

    const clear = () => {
        setSearchQuery({search: '', role: ''});
    }
    
    return (
    <>
        <h1>Employees List</h1>
        <div class="input-group mb-3" style={{width:"50vw"}}>
        <input type="text" class="form-control" placeholder="Điền tên" onChange={handleChange}/>
            <div class="input-group-append">
                <span class="input-group-text"><i class="bi bi-x" onClick={clear}/></span>
            </div>
        </div>
        {!emps.length ? <div class="spinner-border" role="status"/> :
        <ul class="list-group" style={{width:"50vw"}}>
            {emps.map((emp) => <Employee emp={emp} key={emp._id}/>)}
        </ul>}     
    </>)
};

export default Employees;
