import React, { useEffect, useState } from 'react';
import { useDispatch } from 'react-redux';
import { useNavigate } from 'react-router-dom';

import { signin, signup } from "../../actions/authAction"
import { AUTH } from "../../constants/actionTypes"

const initialState = { firstName: '', lastName: '', email: '', password: '', confirmPassword: '' };

const Auth = () => {
    const dispatch = useDispatch();
    const navigate = useNavigate();

    const [form, setForm] = useState(initialState);
    const [isSignup, setIsSignup] = useState(false);
    const [showPassword, setShowPassword] = useState(false);
    useEffect( () => {
        google.accounts.id.initialize({
            client_id: "102581995176-77c0br3ckkgg0s1i7q9pufubqa6d1nm0.apps.googleusercontent.com",
            callback: handleCallbackResponse
        });
        google.accounts.id.renderButton(
            document.getElementById("signInGoogle"),
                { size: "large"}
        );
    }, []);
   
    const switchMode = () => {
        setForm(initialState);
        setIsSignup((prev) => !prev);
        setShowPassword(false);
    };
    const handleChange = (e) => setForm({ ...form, [e.target.name]: e.target.value });
    const handleShowPassword = () => setShowPassword(!showPassword);

    function handleCallbackResponse(res) {
        console.log("Encoded JWT ID token: " + res.credential);
    }
    


    const handleSubmit = (e) => {
        e.preventDefault();
    
        if (isSignup) {
          dispatch(signup(form, history));
        } else {
          dispatch(signin(form, history));
        }
    }

    return (
    <form style={{width:"50vw"}}>
        <h1>{ isSignup ? 'Sign Up': 'Sign In' }</h1>
        {   isSignup && (
            <div class="form-group">
            <label for="exampleInputFname">First Name</label>
            <input type="text" class="form-control" id="exampleInputFname" handleChange={handleChange}/>
            <label for="exampleInputLname">Last Name</label>
            <input type="text" class="form-control" id="exampleInputLname" handleChange={handleChange}/>
            </div>
        )
        }
        <div class="form-group">
            <label for="exampleInputEmail1">Email address</label>
            <input type="email" class="form-control" id="exampleInputEmail1" handleChange={handleChange}/>
        </div>
        <div class="form-group">
            <label for="exampleInputPassword1">Password</label>
            <input type={showPassword ? 'text' : 'password'} class="form-control" id="exampleInputPassword1" handleChange={handleChange}/>
        </div>
        <hr/>
        <div class="g-signin2" data-width="300" data-height="200" data-longtitle="true"></div>
        
        <div id="signInGoogle"></div>
        <button type="submit" class="btn btn-primary me-4">{isSignup ? 'Sign Up' : 'Sign In'}</button>
        <button type="button" class="btn btn-secondary" onClick={switchMode}>
            { isSignup ? 'Already have an account? Sign in' : "Don't have an account? Sign Up" }
        </button>
    </form>
    );
}

export default Auth;
